import React, { useEffect, useState } from 'react'
import {useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row, Col, Image, ListGroup, Button, Form, ListGroupItem } from 'react-bootstrap'

import Loader from '../components/Loader'
import { modelImage } from '../actions/productActions'


function AiModelScreen() {  

  const params = useParams();
  const dispatch = useDispatch();

  const imageData = useSelector(state => state.imageData);
  const { loading, image } = imageData;

  const [inputText, setInputText] = useState('');
  const [submit, setSubmit] = useState(false); 
  const [idRequest, setIdRequest] = useState(0); 

  
  const handleClick = () => {
    console.log(imageData)
    console.log(idRequest)

    if (inputText.length > 5) {      
      setSubmit(true)
      dispatch(modelImage(inputText, idRequest))   
      setIdRequest(1)
    }
    else {console.log(inputText.length)}
    
  }

  /* Image */
  const ImageDiplay = () => {
    return (
      submit ? 
        loading ? <Loader /> 
          : <Image  src={`data:image/jpeg;base64,${image["encoded_image"]}`} fluid/> 
      : <div></div>
    );
  }


  return (
    <div>
      <h1 className='text-white'> Stable Diffusion Model</h1>
      <Row>   
        <Col >
          <Form>
            <Form.Group controlId="formBasicEmail">   
              <Form.Control 
                className="m-2" 
                as="textarea" 
                rows={5} 
                placeholder="Enter the input text..." 
                value={inputText} 
                onChange={e => setInputText(e.target.value)}
              />
              <Button variant="primary" className="float-end my-2" onClick={handleClick}>
                Submit
              </Button>                    
            </Form.Group>            
          </Form>
        </Col> 

        <Col >
          { ImageDiplay() }
        </Col> 
      </Row>    
    </div>
  )
}

export default AiModelScreen
