import React, { useEffect } from 'react';
import { Carousel, Card, Stack, Button } from "react-bootstrap";
import Slider from "react-slick";

import Product from '../components/Product'
import Loader from '../components/Loader'
import Message from '../components/Message'

import { Row, Col} from 'react-bootstrap'
import { listProducts } from '../actions/productActions'
import { useDispatch, useSelector } from 'react-redux'

function MyCarousel() {

  const dispatch = useDispatch()
  const productList = useSelector(state => state.productList)
  const { error, loading, products } = productList

  useEffect(() => {
    dispatch(listProducts())

  }, [])

  const CardContainer = (products) => (
    <div className="cards-container">
      {products.map(product=> (
          <Product key ={product._id} product={product} />
      ))}
    </div>
  );


  const Slide = (products) => {
      var settings = {
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
      }

      return (
        <div className='news'>
          <Slider {...settings}>


            {products.map((product, index) => (
              
              <div key={index}>
                <Col className='c-col-fixed-width p-2' sm={4} md={4} lg={4} xl={4}>
                  <Product product={product} />
                </Col>
              </div>
            ))}

          </Slider>
        </div>
      );
  };


  return (
    <div className="c-last-deals c-gray">
      <h1 className='c-sub-title'>Ultimi prodotti</h1>
        {loading ? <Loader />
          : error ? <Message variant='danger'>{error}</Message>
            :
            <div>
           {Slide(products)}

            {/* <Carousel >
              {products.map((product, index) => { 

                if (index % 3 === 0 && index < products.length - 2) {                   
                  return (
                    <Carousel.Item key={index}>  
                      <Row>
                        <Col className='c-col-fixed-width p-2' sm={4} md={4} lg={4} xl={4}>
                          <Product product={products[index]} />   
                        </Col>
                        <Col className='c-col-fixed-width p-2'  sm={4} md={4} lg={4} xl={4}>
                          <Product product={products[index+1]} />   
                        </Col>
                        <Col className='c-col-fixed-width p-2'  sm={4} md={4} lg={4} xl={4}>
                          <Product product={products[index+2]} />   
                        </Col>                 
                      </Row>
                      
                    </Carousel.Item> 
                  );
                }        
              })}
            </Carousel> */}
            </div>
        }

    </div>
  
  );
}

export default MyCarousel;
